targets.query:
from(bucket: "${buckets}")
|> range(start: v.timeRangeStart, stop:v.timeRangeStop)
|> filter(fn: (r) =>
  r._measurement == "processes" and
  r._field == "total_threads"
)

from(bucket: "${buckets}")
|> range(start: v.timeRangeStart, stop:v.timeRangeStop)
|> filter(fn: (r) =>
  r._measurement == "processes" and
  r._field == "blocked"
)

from(bucket: "${buckets}")
|> range(start: v.timeRangeStart, stop:v.timeRangeStop)
|> filter(fn: (r) =>
  r._measurement == "processes" and
  r._field == "dead"
)

from(bucket: "${buckets}")
|> range(start: v.timeRangeStart, stop:v.timeRangeStop)
|> filter(fn: (r) =>
  r._measurement == "processes" and
  r._field == "idle"
)

from(bucket: "${buckets}")
|> range(start: v.timeRangeStart, stop:v.timeRangeStop)
|> filter(fn: (r) =>
  r._measurement == "mem" and
  r._field == "available"
)
